function fittingLinear(xdata, ydata)
    #=
    model(t, p)=p[1]*t.+p[2];
    p0=[0.5, 0.5];
    fit = curve_fit(model, xdata, ydata, p0);
    #f(x)=fit[1,1]*x+fit[1, 2];
    println("fit: ", fit.param);
    =#
end

