# image is a matrix of values
function image_compression(image, dim)
    decomp=svd(image);
    U=decomp.U
    S=decomp.S
    V=decomp.V
    svd_result = U[:, 1:dim] * Diagonal(S[1:dim]) * V[:, 1:dim]'
    return svd_result
end
