function calculate_deroeck_norm(L,hs, js, gs)
    sum=0
    for i=1:L
        if(i<L)
        sum+=(gs[i]/(2*hs[i]))^2+(hs[i]^2+hs[i+1]^2)*(js[i]/(2*(hs[i]^2-hs[i+1]^2)))^2
        else
        sum+=(gs[i]/(2*hs[i]))^2+(hs[i]^2+hs[1]^2)*(js[i]/(2*(hs[i]^2-hs[1]^2)))^2
        end
    end
    return sum
end

